import axios from "axios";
import { useEffect, useState } from "react"
import { useParams } from "react-router-dom"
import Navbar from "../Navbar/Navbar";
import checkAuth from "../Authenticate/CheckAuth";


function ViewPost() {
    var {postId} = useParams()
    // var [post,setPost] = useState({title:'',content:''})
    var [post,setPost] = useState({Mname:'',Mdesc:'',Mstock:'',Mprice:''})

    useEffect(()=>{
        axios.get('https://medicalstore.mashupstack.com/api/medicine/'+postId,{
            headers: { 'Authorization': "bearer " + user.token }
          }).then(response=>{
            setPost(response.data)
        })
    },[postId]);
    return <div>
        <Navbar/>
        <div className="container">
            <div className="row">
                <div className="col-12">
                    <div className="card">
                        {/* <div className="card-header"><h3>{post.title}</h3></div>
                        <div className="card-body">{post.content}</div> */}
                        <div className="card-header"><h3>{post.Mname}</h3></div>
                        <div className="card-body">
                                <p><strong>Description:</strong> {post.Mdesc}</p>
                                <p><strong>Stock:</strong> {post.Mstock}</p>
                                <p><strong>Price:</strong> {post.Mprice}</p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
}

export default checkAuth(ViewPost);
