import axios from "axios";
import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useSelector } from 'react-redux';

import Navbar from "../Navbar/Navbar";
import checkAuth from "../Authenticate/CheckAuth";


function EditPost() {

    const {postId} = useParams();
    // const [title, setTitle] = useState('');
    // const [content, setContent] = useState('');
    const [Mname, setMname] = useState('');
    const [Mdesc, setMdesc] = useState('');
    const [Mstock, setMstock] = useState('');
    const [Mprice, setMprice] = useState('');
    let navigate = useNavigate();
    useEffect(()=>{
        axios.get('https://medicalstore.mashupstack.com/api/medicine/'+postId).then(response=>{
            setMname(response.data.name);
            setMdesc(response.data.company);
            setMstock(response.data.expiry_date);
        })
    },[postId]);
    function updatePost(){
       

        axios.put('https://medicalstore.mashupstack.com/api/medicine/'+postId,{
            Mname: Mname,
            Mdesc: Mdesc,
            Mstock: Mstock,
            Mprice: Mprice,
        },
        {
            headers:{'Authorization':"Token "+ user.token}
          }).then(response=>{
            alert(response.data.message)
        })
        navigate('/readAPI');
    } var user = useSelector(store=>store.auth.user);
    return(
    <div className="customBg">
        <Navbar/>
        <div className="container w-50 bg-white rounded mt-5 p-3">
            <div className="row">
                <div className="col-8 offset-2">
                    <h1 className="text-center" style={{ fontWeight: 'bold', color: '#531251' }}>Edit Medicine</h1>
                    {/* <div className="form-group">
                        <label>Title:</label>
                        <input type="text" className="form-control" value={title} onChange={(event)=>{setTitle(event.target.value)}}/>
                    </div>
                    <div className="form-group">
                        <label>Content:</label>
                        <textarea className="form-control" value={content} onChange={(event)=>{setContent(event.target.value)}}/>
                    </div> */}
                    <div className="form-group">
                        <label>Medicine Name:</label>
                        <input type="text" className="form-control" value={Mname} onChange={(event)=>{setMname(event.target.value)}}/>
                    </div>
                    <div className="form-group">
                        <label>Description:</label>
                        <textarea className="form-control" value={Mdesc} onChange={(event)=>{setMdesc(event.target.value)}}/>
                    </div>
                    <div className="form-group">
                        <label>Stock left:</label>
                        <input type="text" className="form-control" value={Mstock} onChange={(event)=>{setMstock(event.target.value)}}/>
                    </div>
                    <div className="form-group">
                        <label>Price:</label>
                        <textarea className="form-control" value={Mprice} onChange={(event)=>{setMprice(event.target.value)}}/>
                    </div>
                    <div className="form-group">
                        <button className="btn btn-block customBtnClrAlt" onClick={updatePost}>Submit</button>
                    </div>                    
                </div>
            </div>
        </div>
    </div>
    )
}

export default checkAuth(EditPost);
