import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPenToSquare, faTrash } from '@fortawesome/free-solid-svg-icons';
import '../../App.css';
import Navbar from '../Navbar/Navbar';

function Create() {

    const navigator = useNavigate();

    function doNavigate() {
        navigator('/');
    }

    var [ items, setItems ] = useState ([
        { id : 1, name : "John" },
        { id : 2, name : "David" },
        { id : 3, name : "William" }
]);

const [itemName, setItemName ] = useState(" ");

const handleInputChange = (event ) => {
    setItemName(event.target.value);
};

const handleSubmit = ( event ) => {
    event.preventDefault();
    var x = items.length+1;
    var newItem = {
        id : x,
        name : itemName
        };
    setItems([...items, newItem]);
    setItemName(" ");
    };

    const handleDelete = (id) => {
        const filteredItems = items.filter( (item) => item.id !== id);
        setItems(filteredItems);
    };


    return (
        <div>
            <div className="customBg">
                <Navbar />
                <div className='container customArea'>
                    <p className='customText'>Hola Globo</p>
                    <div className='row'>
                        <div className='col'>
                            <div className='container bg-white rounded'>
                                <form onSubmit={handleSubmit} >
                                    <label> Enter Name </label><br />
                                    <input type="text" value={itemName} onChange={handleInputChange} /><br />
                                    <button className='btn btn-sm text-white rounded my-2' style={{ backgroundColor: '#531251' }}type="submit"> Add </button>
                                </form>
                            </div>
                        </div>
                    </div><br/>
                    <div className='container bg-white rounded'>
                    <table className="table table-bordered table-light">
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Action</th>
                        </tr>
                            {items.map((item) => (
                                    <tr key={item.id}>
                                        <td>{item.id}</td>
                                        <td>{item.name}</td>
                                        <td>
                                            <button className="btn btn-secondary m-1">
                                                <FontAwesomeIcon icon={faPenToSquare} />
                                            </button>
                                            <button className="btn btn-danger m-1" onClick ={() => handleDelete(item.id)}>
                                                <FontAwesomeIcon icon={faTrash} />
                                            </button>
                                        </td>
                                    </tr>
                            ))}
                    </table>
                    </div>
                    
                    <button className='btn text-white rounded mb-2 customBtnClr ' onClick={doNavigate}>Home Page</button>
                </div>
            </div>
        </div>
    );
}

export default Create;
