import React, { useState } from "react";
import Navbar from '../Navbar/Navbar';

function DefaultCRUD() {
    var [ items, setItems ] = useState ([
        { id : 1, name : "John" },
        { id : 2, name : "David" },
        { id : 3, name : "William" }
]);

const [itemName, setItemName ] = useState(" ");

const [editingItemId, setEditingItemId] = useState(null);
  const [editedItemName, setEditedItemName] = useState("");

const handleInputChange = (event ) => {
    setItemName(event.target.value);
};

const handleSubmit = ( event ) => {
    event.preventDefault();
    

    

    if(items.length==0) x=1;
    else    var x=items[items.length-1].id+1;




    var newItem = {
        id : x,
        name : itemName
        };
    setItems([...items, newItem]);
    setItemName(" ");
    };

const handleDelete = (id) => {
    
    
    for(var i=1; i<filteredItems.length;i++){
        if(filteredItems[i].id >= id){
            filteredItems[i].id -= 1;
        }
    }
    const filteredItems = items.filter( (item) => item.id !== id);

    setItems(filteredItems);


  


};

const handleEditItem = (item) => {
    setEditingItemId(item.id);
    setEditedItemName(item.name);
  };

  const handleSaveItem = () => {
    if (editedItemName.trim() !== "") {
      const updatedItems = items.map((item) => {
        if (item.id === editingItemId) {
          return { ...item, name: editedItemName };
        }
        return item;
      });
      setItems(updatedItems);
      setEditingItemId(null);
    }
  };

  const handleCancelEdit = () => {
    setEditingItemId(null);
    setEditedItemName("");
  };


  const handleDeleteItem = (itemId) => {
   
    const updatedItems = items.filter((item) => item.id !== itemId);
    // for(var i=0; i<updatedItems.length;i++){
    //     if(updatedItems[i].id >= itemId){
    //         updatedItems[i].id -= 1;
    //     }
    // }
    
    setItems(updatedItems);
  };

return (
    <div>
    <div> <Navbar/> </div><br/>
    <div className = "container ">
      <div className = "row" >
          <div className= "col-md-8">
        <h2>CRUD</h2>
        <form onSubmit={handleSubmit} >
            <label> Enter Name </label>
            <input type="text" value={itemName} onChange={handleInputChange} required/>
            <button className="btn btn-small btn-success" type="submit"> Add </button>
        </form>
        </div>
        </div>
        </div><br/>
<div className="container">
<table className="table table-bordered table-dark">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {items.map((item) => (
              <tr key={item.id}>
                <td>{item.id}</td>
                <td>
                  {editingItemId === item.id ? ( <input type="text" value={editedItemName} onChange={(e) => setEditedItemName(e.target.value)} />) : 
                    ( item.name)
                  }
                </td>
                <td>
                  {editingItemId === item.id ? (
                    <>
                      <button className="btn btn-primary" onClick={handleSaveItem} >
                        Save
                      </button>
                      <button className="btn btn-secondary" onClick={handleCancelEdit}>
                        Cancel
                      </button>
                    </>
                  ) : (
                    <>
                      <button className="btn btn-primary" onClick={() => handleEditItem(item)}>
                        Edit
                      </button>
                      <button className="btn btn-danger" onClick={() => handleDeleteItem(item.id)}>
                        Delete
                      </button>
                    </>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
</div>
</div>
);
}

export default DefaultCRUD;
